package Array;

public class SumValuesOfAnArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a []= {1,2,3,4,5};
		int sum=0;
		for(int i=0;i<a.length;i++) {
			sum=sum+a[i];// initialize sum, then add with the values of array
			
		}
		System.out.println("Th sum of values: "+sum);
	}

}
