package mypackage;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=5;
		int c;
		c=a;
		System.out.println("The value of c: " +c);
		a=b;
		System.out.println("The value of a: " +a);
		b=c;
		System.out.println("The value of b: " +b);
		

	}

}
